import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUser, faBriefcase, faIdCard, faRss } from '@fortawesome/free-solid-svg-icons';
import { ColoredCircle } from "../misc";
import "./index.scss";
import Roll from "react-reveal/Roll";


class Menu extends Component {
        constructor(props){
            super(props);
            this.state = {
                isMenuVisible: false,
                isMobile: this.props.isMobile,
                scroll: this.props.scroll,
                mobileStyle : {
                    justifyContent: "space-evenly",
                    alignItems: "center",
                    position: "fixed",
                    top: "0",
                    right: "-25rem",
                    bottom: "0",
                    width: "20rem",
                    backgroundColor: "var(--fifth_color)",
                    zIndex: "5000",
                    fontSize: "8rem",
                    transition: "right .2s cubic-bezier(.26,1.45,.87,-0.49)",
                   // transition: "flex-direction .1s .2s",
                    overflowY: "scroll",
                    display: "flex"
                },
                desktopStyle: {

                }
            }
        }

        initiateState(){
            this.setState({
                isMobile: this.props.isMobile,
                scroll: this.props.scroll
            })
            console.log(`init ${this.state.isMobile}`)
        }

        toggleMenu = ()=> {
            this.setState({
                isMenuVisible: (!this.state.isMenuVisible) ? true : false,
            })
    }

    componentDidMount(){
       window.onload = this.initiateState;
    }

    render() {
        return (
            <menu className="menu"
                    style={{...this.state.mobileStyle}}
            >                
                <div className="addCircle"
                    style={
                        {
                            right: (this.state.isMenuVisible) ? "20rem" : "0",
                            display: "block",
                        }
                    }>
                    <ColoredCircle />
                    {/** that div cover the 3 circle and I use it to do the click */}
                    <div className="circle_cover" onClick={this.toggleMenu}>

                    </div>
                </div>
                <Roll left cascade>
                <a href="#home" className="menuIcons">
                    <FontAwesomeIcon icon={faHome} />
                </a>
                <a href="#about" className="menuIcons">
                    <FontAwesomeIcon icon={faUser} />
                </a>
                <a href="#portfolio" className="menuIcons">
                    <FontAwesomeIcon icon={faBriefcase} />
                </a>
                <a href="#contact" className="menuIcons">
                    <FontAwesomeIcon icon={faIdCard} />
                </a>
                <a href="#blog" className="menuIcons">
                    <FontAwesomeIcon icon={faRss} />
                </a>
                    </Roll>                        
            </menu>
        );
    }
}

export default Menu;
