import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUser, faBriefcase, faIdCard, faRss } from '@fortawesome/free-solid-svg-icons';
import { ColoredCircle } from "../misc";
import "./index.scss";
import Roll from "react-reveal/Roll";


class Menu extends Component {
    constructor() {
        super();
        this.state = {
            isMobile: false,
            scrollNum: 0,
            scrollOn: false,
            isMobileMenuVisible: false,
            isDesktopMenu: false,
            style: {
                mobileStyle: {
                    menu: "menu_mobile",
                    circle: "addCircleMobile"
                },
                desktopStyle: {
                    menu: "menu_desktop"
                }
            }
            }
        }

        toggleMenu = ()=> {
            this.setState({
                isMobileMenuVisible: (!this.state.isMobileMenuVisible) ? true : false
            })
        }
        closeMenu = ()=>{
            setTimeout(()=>{
                if(!this.state.scrollOn){
                    this.setState({
                        isMobileMenuVisible: false,
                       isDesktopMenu: true,
                    })
                }
            }, 1000)

        }
        checkScroll = ()=> {
           this.setState({
               scrollNum: window.scrollY,
               scrollOn: (this.state.scrollNum > 150) ? true : false
           })
        }

        checkIfMobile = ()=> {
           if( window.innerWidth < 1000 ){
               this.setState(
                   {
                       isMobile: true
                   }
               )
           }
        }

        componentDidMount(){

        }

    render() {
        const {isMobileMenuVisible, isMobile, style, scrollNum, scrollOn} = this.state;

        window.onload = ()=> {
            this.checkIfMobile();
            this.checkScroll();

            console.log(this.state.isMobileMenuVisible);
            console.log(this.state.isMobile);

        }
        window.onscroll = ()=> {
            this.checkScroll();
            this.closeMenu();
            console.log(scrollNum);
        }
        window.onresize = ()=> {
            this.checkIfMobile();
            this.checkScroll();
            console.log(this.state.isMobile);
        }

        return (

            <menu className={ `menu ${(isMobileMenuVisible) ? style.mobileStyle.menu : style.desktopStyle.menu}`} 
                style = {{
                    right: (isMobileMenuVisible && isMobile || scrollOn) ? "0" : "-25rem"
                }}
            >
                <div className={ (isMobile || scrollOn)? this.state.style.mobileStyle.circle : ""}
                      style={{ 
                        display: (isMobile || scrollOn) ? "block" : "none",
                        right: (isMobileMenuVisible) ? "20rem" : "0"
                    }}
                >
                    <ColoredCircle />
                    {/** that div cover the 3 circle and I use it to do the click */}
                    <div className="circle_cover" onClick={this.toggleMenu}>

                    </div>
                </div>
                <Roll left cascade>
                    <a href="#home" className="menuIcons">
                        <FontAwesomeIcon icon={faHome} />
                    </a>
                    <a href="#about" className="menuIcons">
                        <FontAwesomeIcon icon={faUser} />
                    </a>
                    <a href="#portfolio" className="menuIcons">
                        <FontAwesomeIcon icon={faBriefcase} />
                    </a>
                    <a href="#contact" className="menuIcons">
                        <FontAwesomeIcon icon={faIdCard} />
                    </a>
                    <a href="#blog" className="menuIcons">
                        <FontAwesomeIcon icon={faRss} />
                    </a>
                </Roll>
            </menu>
        );
    }
}

export default Menu;
