import React, { Suspense, Component } from 'react';
import Menu from "./menu";
import { Logo, LoaderSpinner } from "./misc";
import "./App.scss";
import ErrorBoundary from "./ErrorHandler"
import ArticlePage from './Blog/articlePage'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


class App extends Component {
  constructor() {
    super();
    this.state = {
      device: window.screenTop,
      isMobile: false,
      displayMenu : false,
      scroll_position: window.scrollY,
      trackPage: "home",
    }
  }

  checkIfMobile = () => {
    if (window.innerWidth < 1000) {
      this.setState({
        isMobile: true,
      });
    }
  }

  checkScrollLevel = () => {
    this.setState({
      scroll_position: window.scrollY,
    })
  }

  reloadMenu = ()=>{
    setTimeout(() => {
      this.setState({
        displayMenu: false
      })
    }, 6000);
  }

  checkTrackPage = () => {
    if (this.state.scroll_position < 516) {
      this.setState({
        trackPage: "home"
      })
    } else if (this.state.scroll_position > 516 && this.state.scroll_position < 1500) {
      this.setState({
        trackPage: "about"
      })
    } else if (this.state.scroll_position > 1500 && this.state.scroll_position < 2500) {
      this.setState({
        trackPage: "portfolio"
      })
    }
  }

  componentDidMount = () => {

    this.checkIfMobile();
    window.onload = () => {
      this.setState({
        device: document.querySelector(".app"),
      });
      this.checkIfMobile();      
    }

    window.onresize = () => {
      this.checkIfMobile();
    }

    window.onscroll = () => {
      this.checkScrollLevel();
      this.checkTrackPage();
    }

  }

  componentWillUnmount = () => {
    this.checkIfMobile();

    window.onload = () => {
      this.setState({
        device: document.querySelector(".app"),
      });
      this.checkIfMobile();
    }

    window.onresize = () => {
      this.checkIfMobile();
    }

    window.onscroll = () => {
      this.checkScrollLevel();
      this.checkTrackPage();
    }
  }


  render() {
    const Home = React.lazy(() => import('./home'));
    const About = React.lazy(() => import('./about'));
    const Portfolio = React.lazy(() => import('./portfolio'));
    const Blog = React.lazy(() => import('./Blog'));
    const Contact = React.lazy(() => import('./contact'));
    const Footer = React.lazy(() => import('./footer'));

      window.onload = ()=>{
        this.reloadMenu();
      }

    return (
      <div className="app">
            <ErrorBoundary>
              <Suspense fallback={<LoaderSpinner />}>
                <Home />
                <About appState={this.state.trackPage} />
                <Portfolio />
                <Blog mobileState={this.state.isMobile} />
                <Contact />
                <Footer />
                <Switch>
                  <Route path='/blog' component={ArticlePage} /> 
                </Switch>

                <Suspense>
              <div className="menu_container" style={{
                 dispkay: (this.state.displayMenu) ? "block" : "none"
               }}>
                  <Menu isMobile={this.state.isMobile} scroll={this.state.scroll_position} />
                </div>
              </Suspense>

              <Suspense>
                <Logo />
                </Suspense>
              </Suspense>
            </ErrorBoundary>
      </div>
    );
  }
}

export default App;
