import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUser, faBriefcase, faIdCard, faRss } from '@fortawesome/free-solid-svg-icons';
import { ColoredCircle } from "../misc";
import "./index.scss";
import Roll from "react-reveal/Roll";


class Menu extends Component {
    constructor() {
        super();
        this.state = {
            isMobile: false,
            scrollNum: 0,
            isMenuVisibile: false,
            style: {
                mobileStyle: {
                    menu: "menu_mobile",
                    circle: "addCircleMobile"
                },
                desktopStyle: {
                    menu: "menu_desktop"
                }
            }
            }
        }

        toggleMenu = ()=> {
            this.setState({
                isMenuVisibile: (!this.state.isMenuVisibile) ? true : false
            })
        }
        checkScroll = ()=> {
            console.log
        }

        checkIfMobile = ()=> {
           if( window.innerWidth < 1000 ){
               this.setState(
                   {
                       isMobile: true
                   }
               )
           }
        }

        componentDidMount(){

        }

    render() {
        const {isMenuVisibile, isMobile, style, scroll} = this.state;

        window.onload = ()=> {
            this.checkIfMobile();
            console.log(this.state.isMenuVisibile);
            console.log(this.state.isMobile);

        }
        window.onresize = ()=> {
            this.checkIfMobile();
            console.log(this.state.isMobile);
        }

        return (

            <menu className={ (isMenuVisibile) ? style.mobileStyle.menu : style.desktopStyle.menu}
                style = {{
                    right: (isMenuVisibile && isMobile) ? "0" : "-25rem"
                }}
            >
                <div className={ (isMobile)? this.state.style.mobileStyle.circle : ""}
                      style={{ 
                          display: (isMobile || scroll > 5),
                        right: (isMenuVisibile && isMobile) ? "20rem" : "0"
                    }}
                >
                    <ColoredCircle />
                    {/** that div cover the 3 circle and I use it to do the click */}
                    <div className="circle_cover" onClick={this.toggleMenu}>

                    </div>
                </div>
                <Roll left cascade>
                    <a href="#home" className="menuIcons">
                        <FontAwesomeIcon icon={faHome} />
                    </a>
                    <a href="#about" className="menuIcons">
                        <FontAwesomeIcon icon={faUser} />
                    </a>
                    <a href="#portfolio" className="menuIcons">
                        <FontAwesomeIcon icon={faBriefcase} />
                    </a>
                    <a href="#contact" className="menuIcons">
                        <FontAwesomeIcon icon={faIdCard} />
                    </a>
                    <a href="#blog" className="menuIcons">
                        <FontAwesomeIcon icon={faRss} />
                    </a>
                </Roll>
            </menu>
        );
    }
}

export default Menu;
