import React, { Component } from 'react';
import Menu from "./menu";
import Home from "./home";
import About from "./about";
import Porfolio from "./portfolio";
import Blog from "./Blog";
import Contact from "./contact";
import Footer from "./footer";
import { Logo } from "./misc";
import "./App.scss";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
/**
 * let theWidth = window.innerWidth;
 * style={{width: `${theWidth}px`, minWidth: "400px", height: "auto"}}
 */

class App extends Component {
  constructor() {
    super();
    this.state = {
      device: window.screenTop,
      isMobile: false,
      scroll_position: window.scrollY,
      trackPage: "home",
    }
  }

  checkIfMobile = () => {
    if (window.innerWidth < 1000) {
      this.setState({
        isMobile: true,
      });
    }
  }

  checkScrollLevel = () => {
    this.setState({
      scroll_position: window.scrollY,
    })
  }

  checkTrackPage = () => {
    if (this.state.scroll_position < 516) {
      this.setState({
        trackPage: "home"
      })
    } else if (this.state.scroll_position > 516 && this.state.scroll_position < 1500) {
      this.setState({
        trackPage: "about"
      })
    } else if (this.state.scroll_position > 1500 && this.state.scroll_position < 2500) {
      this.setState({
        trackPage: "portfolio"
      })
    }
  }

  componentDidMount = () => {
    this.checkIfMobile();
    window.onload = () => {
      this.setState({
        device: document.querySelector(".app"),
      });
      this.checkIfMobile();
      console.log(`app mobile; ${this.state.isMobile}`);
    }

    window.onresize = () => {
      this.checkIfMobile();
    }

    window.onscroll = () => {
      this.checkScrollLevel();
      this.checkTrackPage();
    }

  }


  render() {
    return (
      <Router>
      <div className="app">
          <Logo />
          <switch>
                <Route path="/">
                  <Home />
                </Route>

                <Route exact path="/#about">
                   <About appState={this.state.trackPage} />
                </Route>

                  <Router>
                     <Porfolio />
                  </Router>

                  <Router path="/blog">
                     <Blog mobileState={this.state.isMobile} />
                  </Router>

                  <Router path="/contact">
                    <Contact />
                  </Router>

                  <Router exact path="/contacts" />
          </switch>
          <div className="menu_container">
              <Menu isMobile={this.state.isMobile} scroll={this.state.scroll_position} />
              <Footer />
          </div>
      </div>
      </Router>
    );
  }
}

export default App;
