import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUser, faBriefcase, faIdCard, faRss } from '@fortawesome/free-solid-svg-icons';
import { ColoredCircle } from "../misc";
import "./index.scss";

class Menu extends Component {
        constructor(){
            super();
            this.state = {
                isMenuVisible: false,
                isMobile: false,
            }
        }

        toggleMenu = ()=> {
            this.setState({
                isMenuVisible: (!this.state.isMenuVisible) ? true : false
            })
    }

    checkMobile = ()=> {
        if(window.innerWidth < 1000) {
            this.setState({
                isMobile: true,
            }
            );
        }else{
            this.setState({
                isMobile: false,
            }
            );
        }
        console.log(`${this.state.isMobile} mobile screen: ${window.innerWidth}`)
    }

    componentDidMount(){
        window.onresize = checkMobile
    }

    render() {
        return (
            <div className="menu"
                style={
                    {
                        display: "flex",


                    }
                }
            >

                <div className="addCircle"
                    style={
                        {
                            right: (this.state.isMenuVisible) ? "20rem" : "0",
                        }
                    }>
                    <ColoredCircle />
                    <div className="circle_cover" onClick={this.toggleMenu}>

                    </div>
                </div>
                <a href="a.com" className="menuIcons">
                    <FontAwesomeIcon icon={faHome} />
                </a>
                <a href="a.com" className="menuIcons">
                    <FontAwesomeIcon icon={faUser} />
                </a>
                <a href="a.com" className="menuIcons">
                    <FontAwesomeIcon icon={faBriefcase} />
                </a>
                <a href="a.com" className="menuIcons">
                    <FontAwesomeIcon icon={faIdCard} />
                </a>
                <a href="a.com" className="menuIcons">
                    <FontAwesomeIcon icon={faRss} />
                </a>

            </div>
        );
    }
}

export default Menu;
