import React, { Component } from 'react';
import "./style.scss";
import profilePhoto from "../images/logo/man.jpg";
import { SocialLogos, Buttons, ColoredCards } from "../misc";
import { render } from 'sass';

class Home extends Component {
    constructor() {
        super();
        this.state = {
            Theheight: window.innerHeight,
            Thewidth: window.innerWidth
        }
    }

    colorSepator = () => {
        this.setState(
            {
              Theheight: window.innerHeight * 0.01,
              Thewidth: window.innerWidth,
            }
        )
          console.log("work" + this.state.Theheight);
      }
    componentDidMount() {
        window.onload = this.colorSepator;
        window.onresize = this.colorSepator;

    }

    render() {
        return (
            <div className="home-container">
                <div className="home-colorSepator" style={{ height: (tis.state.Thewidth > 1000) ? `${this.state.Theheight}px` : "25rem" }} ></div>
                <div className="home">
                    <div className="home-profilePhoto_container">
                        <div className="image">
                            <img src={profilePhoto} alt="jerry" title="Jerry Prophete" />
                        </div>
                    </div>

                    <div className="home-socialMedia">
                        <SocialLogos isTrue={true} />
                    </div>

                    <div className="home-para">
                        <p>
                            <span className="p1">Hey There!</span>
                            <span className="p2">I am Jerry Prophete.</span>
                            <span className="p3">I can build your website.</span>
                        </p>
                        {/* backgroundColor: this.props.backColor,
                color: this.props.color,
                {this.props.text} */}

                    </div>
                    <div className="theButton">
                        <Buttons
                            backColor="var(--sixth_color)"
                            text="Hire ME"
                            color="var(--first_color)"
                            link="b.com"
                        />
                    </div>

                    <div className="colored_section">
                        <ColoredCards color="var(--first_color)" />
                        <ColoredCards color="var(--seventh_color)" />
                        <ColoredCards color="var(--sixth_color)" />
                    </div>
                </div>
            </div>
        );
    };
}
export default Home;
