import React, { Component } from 'react';
import Menu from "./menu";
import Home from "./home";
import About from "./about";
import Porfolio from "./portfolio";
import { Logo } from "./misc";
import "./App.scss";
/**
 * let theWidth = window.innerWidth;
 * style={{width: `${theWidth}px`, minWidth: "400px", height: "auto"}}
 */

class App extends Component {
  constructor() {
    super();
      this.state = {
          isMobile: false,
          scroll_position: window.scrollY
      }
  }

  checkIfMobile(){
    if(window.innerWidth < 1000 || this.state.scroll_position > 5) {
      this.setState({
        isMobile: true,
      });
  }
}

checkScrollLevel(){
  this.setState({
    scroll_position: window.scrollY
  })
}

  componentDidMount = ()=>{
    window.onload = this.checkIfMobile;

    window.onscroll = ()=> {
      this.checkScrollLevel();
      console.log(this.state.scroll_position);
    }

   console.log(this.state.scroll_position);
  }


  render() {
    return (
      <div className="app">
        <Logo />
          <div className="menu_container">
            <Menu mobie={this.state.isMobile} scroll = {this.state.scroll_position}/>
          </div>

        <Home />
        <About />
        <Porfolio />
      </div>
    );
  }
}

export default App;


