import React, { Component } from 'react';
import "./blog.scss";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faRss, faAngleDoubleRight } from '@fortawesome/free-solid-svg-icons';
import Bounce from "react-reveal/Bounce";
import {Poster, Buttons, InputField} from "../misc";


class Blog extends Component {
    constructor(props){
        super(props)
        this.state = {
            arrowPushMenu : (this.props.isMobile) ? 10 : "",
            pushMenuBlog: (this.props.isMobile) ? 25 : ""
        }
    }

        sideMenuBlog = ()=> {
            this.setState(
                {
                  arrowPushMenu: (this.state.arrowPushMenu === 5 && this.props.isMobile) ? 10 : 5,
                  pushMenuBlog:  (this.state.pushMenuBlog === 25 && this.props.isMobile) ? 0 : 25,
                }
            )
        }



    render() {
        const {arrowPushMenu, pushMenuBlog} = this.state;
        return (

            <div className="blog_container">
                <Bounce delay={500}>
                <h1 className="title"> Blog <span><FontAwesomeIcon icon={faRss} /></span></h1>
                </Bounce>
                <div className="contents_container">
                    <div className="poster_container">
                    <Poster isMobile={true} shadow="0rem 5rem 10rem 0.5rem black"/>
                    </div>
                    <div className="article_list" style={{left: (this.props.isMobile) ? `-${pushMenuBlog}rem` : "none"}}>
                        <ul>
                         <h4>Recent Blog Posts</h4>
                            <li>Life on hearth</li>
                            <li>Life on hearth</li>
                            <li>Life on hearth</li>
                            <li>Life on hearth</li>
                            <li>Life on hearth</li>
                            <li>Life on hearth</li>
                            <li>Life on hearth</li>
                            <li>Life on hearth</li>
                            <li>Life on hearth</li>
                            <li>Life on hearth</li>
                            <div></div>
                        </ul>
                    </div>

                    <div className="doubleArrow" style={{ transform: `translateX(-${arrowPushMenu}rem)`}} onClick={this.sideMenuBlog}>
                        <FontAwesomeIcon icon={faAngleDoubleRight} style={{transform: (this.props.isMobile && this.state.arrowPushMenu === 10) ? "rotateY(180deg)" : "rotateY(0deg)"}} />
                    </div>
                    <div className="visit">
                        <Buttons    
                            link={"#home"}
                            text="visit"
                            backColor="var(--second_color)"
                            width="10rem"
                            textSize="2rem"
                            color="var(--first_color)"
                            position="relative"/>
                    </div>

                    <div className="signIn_emailList_message">
                    <p>
                          Sign up to my email list to know about my latest blog post and to receive update from me. 
                    </p>

                    <form>
                         <InputField type="email" label="Email"/>
                         <Buttons backColor="var(--second_color)" text="Subscribe" width="12rem" height="4rem" textSize="2.5rem" position="relative" top="2rem"/>
                    </form>
                    </div>

                    {/* backgroundColor: props.backColor,
                    color: props.color, */}
                </div>
            </div>
        );
    }
}

export default Blog;
